mapper.select:
SELECT *
FROM topic_throttled_metrics
WHERE cluster_id = #{clusterId}
    AND topic_name = #{topicName}
    AND app_id = #{appId}
    AND gmt_create BETWEEN #{startTime} AND #{endTime}

SELECT *
FROM topic_throttled_metrics
WHERE cluster_id = #{clusterId}
    AND app_id = #{appId}
    AND gmt_create BETWEEN #{startTime} AND #{endTime}

SELECT * FROM topic_throttled_metrics
WHERE cluster_id = #{clusterId}
AND gmt_create > #{afterTime}
